    // the instructions for ChatGPT on how the different parts are structured and how and when to summarize them
    public static string GetInitialInstructions()
    {
        return "The total length of the content that I want to send you is too large to send in only one piece.\n\nFor sending you that content, I will follow this rule:\n\n[START PART 1/10]\nthis is the content of the part 1 out of 10 in total\n[END PART 1/10]\n\nThen you just answer: 'Received part 1 / 10'.\n\nAnd when I tell you 'ALL PARTS SENT', then you can continue processing the data and answering my requests.\n\n";
    }
    
    public static List<Dictionary<string, string>> SplitPrompt(string text, int splitLength)
    {
        if (splitLength <= 0)
        {
            throw new ArgumentException("Max length must be greater than 0.");
        }

        int numParts = (int)Math.Ceiling((double)text.Length / splitLength);
        List<Dictionary<string, string>> fileData = new List<Dictionary<string, string>>();

        for (int i = 0; i < numParts; i++)
        {
            int start = i * splitLength;
            int end = Math.Min((i + 1) * splitLength, text.Length);

            string content = "";

            if (i == numParts - 1)
            {
                content = $"[START PART {i + 1}/{numParts}]\n{text.Substring(start, end - start)}\n[END PART {i + 1}/{numParts}]";
                content += "\nALL PARTS SENT. Now you can continue processing the request.";
            }
            else
            {
                content = $"Do not answer yet. This is just another part of the text I want to send you. Just receive and acknowledge as \"Part {i + 1}/{numParts} received\" and wait for the next part.\n[START PART {i + 1}/{numParts}]\n{text.Substring(start, end - start)}\n[END PART {i + 1}/{numParts}]";
                content += $"\nRemember not answering yet. Just acknowledge you received this part with the message \"Part {i + 1}/{numParts} received\" and wait for the next part.";
            }

            fileData.Add(new Dictionary<string, string>()
            {
                { "name", $"split_{(i + 1).ToString().PadLeft(3, '0')}_of_{numParts.ToString().PadLeft(3, '0')}.txt" },
                { "content", content }
            });
        }

        return fileData;
    }


